#include <stdio.h>
#include <stdlib.h>


char	**ft_split(char const *s, char c);


//Global variables
//**************
int row_max = 0;
int col_max = 0;
//**************



void map_size_get(char **map)
{
	int i=0;
	int j =0;
	while (map[0][j])
		j++;
	while (map[i])
		i++;
	row_max = i;
	col_max = j;
}


int roll_checker(char **map, int i, int j)
{
	int roll_count = 0;
	
	if (j < col_max - 1 && map[i][j+1] == '@')	//E
		roll_count++;
	
	if (j < col_max - 1 && i < row_max - 1 && map[i+1][j+1] == '@')	//SE
		roll_count++;	
						
	if (i < row_max - 1 && map[i+1][j] == '@')	//S
		roll_count++;	
					
	if (j > 0 && i < row_max - 1 && map[i+1][j-1] == '@')	//SW
		roll_count++;	
						
	if (j > 0  && map[i][j-1] == '@')	//W
		roll_count++;	
					
	if (j > 0 && i > 0 && map[i-1][j-1] == '@')	//NW
		roll_count++;	
						
	if (i > 0 && map[i-1][j] == '@')	//N
		roll_count++;	
					
	if (j < col_max - 1 && i > 0 && map[i-1][j+1] == '@')	//NE
		roll_count++;	
	
	if (roll_count < 4)
		return (623);
	return (0);				
}

