#include <stdio.h>
#include <stdlib.h>
#include <math.h>


typedef struct s_box
{
	double x;
	double y;
	double z;
	int  circuit;
} t_box;

typedef struct s_distance
{
	double distance;
	int a;
	int b;

} t_distance;

t_distance dist(t_box *box_array, int a, int b)
{
	
	t_distance result;
	
	result.distance = sqrt((box_array[a].x - box_array[b].x)*(box_array[a].x - box_array[b].x)
		+ (box_array[a].y - box_array[b].y)*(box_array[a].y - box_array[b].y)
		+ (box_array[a].z - box_array[b].z)*(box_array[a].z - box_array[b].z));
	result.a = a;
	result.b = b;
	
	return (result);

}


int compare_distances(const void *a, const void *b)
{
    t_distance *dist_a = (t_distance *)a;
    t_distance *dist_b = (t_distance *)b;
    
    if (dist_a->distance < dist_b->distance)
        return -1;
    else if (dist_a->distance > dist_b->distance)
        return 1;
    return 0;
}



